package com.alibaba.cloud.dubbo.service;

import com.alibaba.cloud.dubbo.WelcomeAd;
import com.enjoy.cores.constants.DubboConstants;
import com.enjoy.cores.result.Results;
import com.enjoy.cores.utils.JacksonUtil;
import lombok.extern.slf4j.Slf4j;
import org.apache.dubbo.config.annotation.DubboService;
import org.apache.dubbo.rpc.RpcContext;

/**
 * @program: spring-cloud-dubbo
 * @description:
 * @author: LiZhaofu (filter={"dubboTraceIdFilter"})
 * @create: 2020-08-01 11:39
 **/

@DubboService(protocol = "dubbo",filter = {DubboConstants.DUBBO_FILTER},loadbalance = DubboConstants.PROVIDER_LOAD_BALANCE,
        timeout = DubboConstants.PROVIDER_TIMEOUT,retries = DubboConstants.PROVIDER_RETRIES)
@Slf4j
public class DubboServiceImpl implements IDubboService{

    @Override
    public Results sayHello(String name) {
        String retStr = name + ", request from consumer: " + RpcContext.getContext().getRemoteAddress();
        log.info(retStr);

        return Results.ok(retStr);
    }

    @Override
    public Results welcomeAd() {
        WelcomeAd welcomeAd =new WelcomeAd();

        welcomeAd.setType("img");
        welcomeAd.setTitle("欧派全屋定制");
        welcomeAd.setImg("https://yuexiang-video.oss-cn-beijing.aliyuncs.com/2020/06/29/16-57-2507251153116897");
        welcomeAd.setContent("https://www.oppein.cn/");
        welcomeAd.setShowUrl("url");
        welcomeAd.setDuration(3000);

        Results<WelcomeAd> results = Results.ok(welcomeAd);
        log.info(JacksonUtil.toJson(results));

        return results;
    }

    @Override
    public Results bigData() {
        //25 KB (25,600 字节)
        String longStr="";
        StringBuilder sb = new StringBuilder();
        sb.append(longStr);
        sb.append(longStr);
        sb.append(longStr);
        sb.append(longStr);
        //100 KB (102,400 字节)
        return Results.ok(sb);
    }

    @Override
    public Results exception() {
        return Results.ok(1/0);
    }

    @Override
    public Results blockService(int seconds) {
        log.info("seconds:{}",seconds);
        try {
            Thread.sleep(seconds*1000);
        } catch (InterruptedException e) {
            e.printStackTrace();
        }
        log.info("block success!");
        return Results.ok();
    }
}
